import React from "react";

export class Counter extends React.Component {
  state = {
    count: this.props.initialvalue,
  };

  constructor(props) {
    super(props);

    setInterval(() => {
      this.setState((state) => {
        return {
          count: this.state.count + 1,
        };
      });
    }, this.props.incrementInterval);
  }

  render() {
    return (
      <div>
        <h1>Count: {this.state.count}</h1>
      </div>
    );
  }
}
